const formatJSON = () => {
  // Convert JSON object to formatted string
   const textarea = document.getElementById('passwordPolicy');
    try {
      // Get the text from the textarea
      const jsonString = textarea.value;

      // Parse the text to a JSON object
      const jsonObject = JSON.parse(jsonString);

      // Convert the JSON object to a formatted string
      const formattedJson = JSON.stringify(jsonObject, null, 2);

      // Set the formatted string back to the textarea
      textarea.value = formattedJson;
    } catch (error) {
      console.error('Invalid JSON string:', error);
      alert('Invalid JSON string. Please correct it and try again.');
    }
}
const start = () => {
  document.getElementById('configForm').addEventListener('change', formatJSON);
  formatJSON();
}

if (document.readyState === "loading") {
