   email.addEventListener("keyup", inputChanged, false);
} else
   if(window.attachEvent) {
      // IE
      email.attachEvent("keyup", inputChanged);
   }


function checkInputValid(val) {
      return /[+\w0-9._-]+@[\w0-9._-]+\.[\w0-9_-]+/.test(val);
}


function inputChanged() {
   let submitButton  = document.getElementById('submit');
   if (!checkInputValid(email.value)) {
      submitButton.setAttribute("disabled", "true");
      if(document.getElementById("skerrormessage")){
         document.getElementById("skerrormessage").style.display="block";
      }

   }else{
         submitButton.removeAttribute("disabled");
         if(document.getElementById("skerrormessage")){
            document.getElementById("skerrormessage").style.display="none";
         }
      }
}

inputChanged();

