sktemplate.script:
const phone = document.getElementById("phone");
phone.addEventListener("input", myFunction);
function myFunction() {  phone.value === "" ? phone.parentElement.classList.add("placeholder-shown") : phone.parentElement.classList.remove("placeholder-shown") ;}

const phoneCodeDropdown = document.getElementById("phone-input-code-dropdown");
const countryCodeInput = document.getElementById("countryCodeInput");
countryCodeInput.value = "+1";

const phoneCodeList = document.getElementById("code-dropdown-list");
const phoneCode = document.getElementById("phone-input-code");
countryCodeInput.addEventListener("click", openList);
const closeListBtn =  document.getElementById("close-list");
closeListBtn.addEventListener("click", closeList);
const listContainer = document.getElementById("list-container");

var items = document.getElementsByClassName("dropdown__option");
for (var i = 0; i < items.length; i++) {
  items[i].addEventListener("click", selectClick);
}

function closeList() {
    phoneCodeDropdown.classList.remove("dropdown--open");
    phoneCode.classList.remove("phone-input__code--open");
    phoneCodeList.classList.add("none");
}
function selectClick(event) {
    let code = event.target.innerHTML.indexOf("+");
    countryCodeInput.value = event.target.innerHTML.substring(code);
    closeList(event);
}
function openList() {
    phoneCodeDropdown.classList.add("dropdown--open");
    phoneCode.classList.add("phone-input__code--open");
    phoneCodeList.classList.remove("none");
}

const search = document.getElementById("search-input");
search.addEventListener("input", searchList);
function searchList() {  
    const searchValue = search.value.toLocaleLowerCase();
    for (var i = 0; i < items.length; i++) {
        if(items[i].innerHTML.toLocaleLowerCase().indexOf(searchValue)>-1 || searchValue.length < 1){
            items[i].classList.remove("none");
        }else{
            items[i].classList.add("none");
        }

    }
}

