img {
  width: 80px;
  height: 80px;
  margin: auto;
}

.org-logo {
  text-align: center;
}

body, .page {
  background-color: #ededed !important;
}

.text-description {
  color: #595959;
}

button {
  border: none;
  height: 35px;
}

button.approve {
  background-color: #277BA5;
  border-radius: 2px;
  color: white;
}

button.decline {
  background-color: transparent;
  color: #277BA5;
}

.result-title {
  color: #595959;
  font-size: 22px;
  font-style: normal;
  font-weight: 400;
  line-height: normal;
  text-align: center;
}

.result-description {
  color: #595959;
  font-size: 15px;
  font-style: normal;
  font-weight: 400;
  line-height: normal;
  text-align: center;
}

.icon-alert {
  text-align: center;
  font-size: 6rem;
}

