const getOrigin = () => {
    const origin = window.location.origin;
    document.getElementById("origin").value = origin;
    document.getElementById("rpid").value = origin.replace(/^https?:\/\//, '');

    document.getElementById('autoSubmit').click();
}

// If the page is still loading, wait for it to finish before calling the function
if (document.readyState === "loading") {
    document.addEventListener("DOMContentLoaded", getOrigin);
} else {
    // If the page has already finished loading, call the function immediately
    getOrigin();
}

const setFieldFocus = (id) => {
    const element = document.getElementById(id);
    if (element) {
        element.focus();
    }
}

const start = () => {
    setFieldFocus("username")
}

if (document.readyState === "loading") {
    // Loading hasn't finished yet
    document.addEventListener("DOMContentLoaded", start);
} else {
    // `DOMContentLoaded` has already fired
    start();
}

