/**
 * Calculates if a birthdate meets legal requirements 
 * @param {string} params.dob - The birthdate to validate (format: YYY-MM-DD)
 * @param {number} params.legalAge - The number of years to be legal
 * @returns {boolean} Returns if successful, returns a false branch if dob does not meet requirements
 */
module.exports = isLegalAge = async ({ params }) => {
	const { dob, legalAge } = params;

	// Both values are required to determine outcome
	if (!dob || !legalAge) {
		return false;
	}

	const ageDetails = calculateAgeDetails(dob);

	// If unable to parse date, return false
	if (ageDetails === null) {
		return false;
	}

	const isLegalAge = ageDetails.years > legalAge || (ageDetails.years === legalAge && (ageDetails.months > 0 || ageDetails.days > 0));

	if (isLegalAge) {
		return
	} else {
		return false;
	}
}

const calculateAgeDetails = (dob) => {
	const birthDate = new Date(dob);

	// Return null if the date is invalid
	if (isNaN(birthDate.getTime())) {
		return null;
	}

	const currentDate = new Date();
	let years = currentDate.getFullYear() - birthDate.getFullYear();
	let months = currentDate.getMonth() - birthDate.getMonth();
	let days = currentDate.getDate() - birthDate.getDate();

	if (days < 0) {
		// Adjust days and months
		const previousMonth = new Date(currentDate.getFullYear(), currentDate.getMonth(), 0);
		days += previousMonth.getDate();
		months--;
	}

	if (months < 0) {
		// Adjust months and years
		months += 12;
		years--;
	}

	return { years, months, days };
}

