// Focus on the first input element on the page
const focusOnFirstInputElement = () => {
    const element = document.querySelector("input");

    // Set focus on the first input element, if it exists
    if (element) {
        element.focus();
    }
};

const makePasswordToggle = () => {
  const passwordInputs = document.querySelectorAll("input[type='password']");

  passwordInputs.forEach((passwordInput) => {
    const container = passwordInput.parentElement;
    const toggler = document.createElement("button");
    toggler.setAttribute("tabindex", "-1");
    toggler.type = "button";
    toggler.setAttribute("aria-label", "Show/Hide Password");
    toggler.className = "btn mdi mdi-eye-off-outline position-absolute end-0 top-50 translate-middle-y";
    container.appendChild(toggler);

    function showHidePassword() {
      const passwordType = passwordInput.type === "password" ? "text" : "password";
      passwordInput.type = passwordType;
      toggler.classList.toggle("mdi-eye-outline");
      toggler.classList.toggle("mdi-eye-off-outline");
      passwordInput.focus();
    }

    toggler.addEventListener("click", showHidePassword);
  });
};

const start = () => {
  makePasswordToggle();
  focusOnFirstInputElement();
};

if (document.readyState === "loading") {
  // Loading hasn't finished yet
  document.addEventListener("DOMContentLoaded", start);
} else {
  // `DOMContentLoaded` has already fired
  start();
}

