module.exports = a = async ({params}) => {

	const devices =  params.devices;
	for (var i = 0 ; i < devices.length ; i++){
		if (devices[i].id == params.userSelectedDevice) {
			const type = devices[i].type;
			var message = "";
			switch(type){
				case "PINGID_MOBILE" : message = "Open PingID mobile app to get a one-time passcode, and enter it here."
				break;
				case "PINGID_DESKTOP" : message = "Open PingID desktop app to get a one-time passcode, and enter it here."
				break;
				case "EMAIL" : message = "Enter the one-time passcode sent to your Email."
				break;
				case "VOICE" : message = "Enter the one-time passcode you received by voice call."
				break;
				case "SMS" : message = "Enter the one-time passcode sent to you by SMS."
				break;
				case "TOTP" : message = "Open your authenticator app to get a one-time passcode, and enter it here."
				break;
				case "OATH_TOKEN" : message = "Enter the one-time passcode displayed on your token."
				break;
				case "YUBIKEY" : message = "To generate and enter a one-time passcode, insert your YubiKey and tap it."
				break;
			}
			return {'message' : message} 
		}
	}
}
