module.exports = a = async ({params}) => {
	const devices =  params.userDevices;
	devices.map(device =>{
	    return device.nickname;
    })

    const charLimit = 253;
    var newLine ;
    switch(params.newLineChar){
        case "\\n" : newLine = "\n";
        break;
        case "\\r\n" : newLine = "\r\n";
        break;
        case "<br>" : newLine = "<br>";
        break;
        default : newLine = " ";

    }


    // Create a string of devices for the user to select from.
    // Appropriate message is shown if the user selected wrong device/input.
    var message ;
    params.wrongDevice ? message = "Authentication failed. Try again with a different device:" + newLine : message = "Select the authenticating device you want to use:" + newLine ;
    var length = 0;
    for (var i = 0 ; i < devices.length ; i++){
        message += i + 1 + ". " + devices[i].nickname + newLine;
        length += devices[i].nickname.length;
    }



    // Create a message for the user to select an authentication device from a list
    // If the message is larger than 253 characters, apply a truncating algorithm.
    if (message.length <= charLimit) {
        return {'message': message}
    } else {
        var truncatedMessage = getTruncatedMessage(message, devices, charLimit, length);
        return {'message': truncatedMessage} 
    }


    /* 
        Function to truncate the device list message in case it exceeds 253 characters, to match the RADIUS Reply-Message limit.
        The algorithm will truncate the device nicknames to a prefix ending with "..". 
        Here is an example to explain the change (it does not reach the 253 limit, but helps visualize the modified text):

        The function will modify the following device list message input:
        "Select the authenticating device you want to use:
        1. My email device 
        2. My mobile app device 
        3. My SMS device"

        The output of the function will be:
        "Select the authenticating device you want to use: 
        1. My email.. 
        2. My mobil.. 
        3. My SMS d.."
    */
    function getTruncatedMessage(originalMessage, devices, charLimit, sumOfNicknames) {
        var totalDevices = devices.length;
        var fixedSize;
        var truncatedMessage;
        params.wrongDevice ? fixedSize = (originalMessage.length + "Authentication failed. Try again with a different device:".length) - sumOfNicknames : fixedSize = originalMessage.length - sumOfNicknames;
        var availableLengthForNickNames = charLimit - fixedSize;
        var availableLengthForNickName = availableLengthForNickNames / totalDevices;
        var totalRemainder = availableLengthForNickNames % totalDevices;
        params.wrongDevice ? truncatedMessage = "Authentication failed. Try again with a different device:" + newLine : truncatedMessage = "Select the authenticating device you want to use:" + newLine ;
        var i = 1;

        for (const device of devices) {
            var nickname = device.nickname;
            var truncatedNickName = getTruncatedNickName(nickname, totalRemainder, availableLengthForNickName);
            truncatedMessage += i + ". " + truncatedNickName + newLine ;

            if(nickname != null && nickname.length > availableLengthForNickNames && totalRemainder > 0) {
                totalRemainder = totalRemainder - 1;
            }
            i++;
        }
        return truncatedMessage;
    }

    function getTruncatedNickName(nickname, totalRemainder, availableLengthForNickName) {
        if (totalRemainder > 0) {
            availableLengthForNickName = availableLengthForNickName + 1;
        }

        if (nickname == null || nickname.length <= availableLengthForNickName) {
            return nickname;
        }

        return nickname.substring(0, availableLengthForNickName - 2) + "..";
    }

}

