// Verify if the account is present, it's enabled
module.exports = a = async ({params}) => {
	const userid = params.userid;
	const enabled = params.enabled;

	// If there is a userid and it is not enabled, throw error
	if (userid && !enabled){
		return false;
	}

	return;
}

