module.exports = a = async ({ params }) => {
	const newPassword = params.newPassword;
	const verifyNewPassword = params.verifyNewPassword;

	if (!newPassword || !verifyNewPassword || (newPassword !== verifyNewPassword)) {
		return false;
	}

	return { "success": true};
}

