module.exports = a = async ({ params }) => {
  const country = params.country;

  // PingOne requires a 2 character country code, convert it if country is 3 characters long
  let countryCode = "";
  if (country && country.length === 3) {
    countryCode = convertToTwoCharCode(country);
  }

  const addressObject = {
    "streetAddress": params.addressStreet,
    "locality": params.addressCity,
    "region": params.addressState,
    "postalCode": params.addressZip,
    "countryCode": countryCode,
  };

  return { "addressObject": addressObject }
}

