/**
 * Masks a phone number or email address.
 * @param  {Object} params - The parameters object containing an email or phone.
 * @returns {Object} An object with a maskedValue property containing the masked email or phone.
 */
module.exports = function maskContactInfo({ params: { email, phone } = {} }) {
    let maskedValue;

    if (email) {
        const [username, domain] = email.split("@");
        const maskedUsername =
            username.charAt(0) + "*".repeat(username.length - 2) + username.slice(-1);
        maskedValue = `${maskedUsername}@${domain}`;
    } else if (phone) {
        const numericPhoneNumber = phone.replace(/\D/g, '');
        maskedValue = `***-***-${numericPhoneNumber.slice(-4)}`;
    }

    return { maskedValue };
}

