module.exports = a = async ({ params }) => {
  let usableDevices = JSON.parse(params.devices);

  usableDevices = usableDevices.map(device => {
    if (device.type === "FIDO2" && device?.attributes?.previousDeviceType) {
      return { ...device, type: device.attributes.previousDeviceType }
    }
    return device;
  });

  if (params.webAuthenSupport === "NONE") {
    usableDevices = usableDevices.filter(device => {
      return device.type !== "FIDO2";
    });
  }

  usableDevices = usableDevices.filter(device => {
    return params.allowedTypes.includes(device.type);
  })

  usableDevices = usableDevices.filter(device => {
    return device.coolDownExpiresAt == undefined;
  });

  return { usableDevices: usableDevices, count: usableDevices.length, canChangeDevice: usableDevices.length > 1 }
}

module.exports = a = async ({ params }) => {
  String.prototype.maskEmail = function() {
    const [username, domain] = this.split("@");
    const maskedUsername =
      username.charAt(0) + "*".repeat(username.length - 2) + username.slice(-1);
    return maskedUsername + "@" + domain;
  };

  String.prototype.maskPhoneNumber = function() {
    // Strip all non-numeric characters from the phone number
    const numericPhoneNumber = this.replace(/\D/g, '');

    // Mask the first six digits of the phone number
    const maskedPhoneNumber = '***-***-' + numericPhoneNumber.slice(-4);

    return maskedPhoneNumber;
  };

  var usableDevices = JSON.parse(params.devices);

  for (let i = 0; i < usableDevices.length; i++) {
    if (usableDevices[i].type === "EMAIL") {
      usableDevices[i].email = usableDevices[i].email.maskEmail();
    } else if (usableDevices[i].type === "SMS") {
      usableDevices[i].phone = usableDevices[i].phone.maskPhoneNumber();
    }
  }

  return { 
    maskedUsableDevices: usableDevices
  }
}

