/*
 Update the devices array to include a new properties 
 used when rendering device list.
*/

/*
* deviceTypes
* title: Display type for the device
* icon: Icon used on forms
* description: Brief overview of device type
* maxDevices: Number of devices a user can have of that type (-1 = unlimited, or up to policy max)
*/
let deviceTypes = {
	SMS: { title: "Text Message", icon: "mdi-comment-text-outline", description: "Text Message sent to", maxDevices: 1 },
	VOICE: { title: "Voice", icon: "mdi-phone-outline", description: "Voice call made to", maxDevices: 1 },
	EMAIL: { title: "Email", icon: "mdi-email-outline", description: "Email sent to", maxDevices: 1 },
}

// Device types that require masking
const SMS = "SMS";
const VOICE = "VOICE";
const EMAIL = "EMAIL";


module.exports = a = async ({ params }) => {
	const deviceType = params.deviceType;
	const deviceAddress = params.deviceAddress;

	// If devices is not passed, head to error branch in flow
	if (!deviceType || !deviceAddress) {
		return false;
	}

	let deviceMetaData = {}

	deviceMetaData.icon = deviceTypes[deviceType].icon;
	deviceMetaData.title = deviceTypes[deviceType].description;

	if (deviceType === SMS || deviceType === VOICE) {
		deviceMetaData.title += `: ${maskPhone(deviceAddress)}` 
	} else if (deviceType === EMAIL) {
		deviceMetaData.title += `: ${maskEmail(deviceAddress)}` 
	}

	return { 'deviceMetaData': deviceMetaData }
}

// Mask email
const maskEmail = (email) => {
	const [username, domain] = email.split("@");
	const maskedUsername =
		username.charAt(0) + "*".repeat(username.length - 2) + username.slice(-1);
	return maskedUsername + "@" + domain;
};

//Mask phone number
const maskPhone = (phone) => {
	const numericPhoneNumber = phone.replace(/[^\d+]/g, "");

	// Mask the first six digits of the phone number
	const maskedPhoneNumber = "***-***-" + numericPhoneNumber.slice(-4);

	return maskedPhoneNumber;
};

