/**
 *
 * FIDO Registration
 *
 * Creates a new credential to register with PingOne MFA using WebAuthn
 *
 */

/**
 * If the string is valid JSON, parse it and return the object. If it's not
 * valid JSON, return the string or throw an error
 *
 * @param strVal - The stringified JSON object that you want to parse
 * @returns The parsed JSON as an object
 */
const parseJSONIfNeeded = (strVal) => {
  try {
    return JSON.parse(strVal);
  } catch (error) {
    if (error.name === 'SyntaxError') {
      if (error.message === '"[object Object]" is not valid JSON') {
        // In most cases, this means that the method argument was given as a
        // JSON object, so we can just proceed
        // But, you can add debugging here if you need to investigate further
        return strVal;
      } else {
        // This means that we didn't get an object passed into the method as
        // an object and there's a syntax problem with it
        throw new Error(
          'JSON parsing failed due to improper JSON syntax found in public key credential request options string.',
          {cause: error}
        );
      }
    } else {
      // This is unexpected and needs further debugging
      throw new Error('Unexpected error. Please try again.', {
        cause: error,
      });
    }
  }
};

/**
 * It converts a list of credentials from the format given in the public key
 * credentials object to the format used by the WebAuthn API
 *
 * @param list - The list of credentials to convert.
 * @returns An array of credential objects which includes the type of
 * credential and its ID
 */
const credentialListConversion = (list) => {
  const credList = [];

  for (let i = 0; i < list.length; i++) {
    const cred = {
      type: list[i].type,
      id: new Uint8Array(list[i].id),
    };

    if (list[i].transports) {
      cred.transports = list[i].transports;
    }

    credList.push(cred);
  }

  return credList;
};

/**
 * It converts a binary array to a base64 string
 *
 * @param bin - The binary data to convert to base64
 * @returns A string of base64 encoded data
 */
const toBase64Str = (bin) => {
  return btoa(String.fromCharCode.apply(null, new Uint8Array(bin)));
};

/**
 * It takes an error object and updates the error message element with the error
 * message
 *
 * @param error - The error object or message to display.
 */
const updateErrorMessageText = (error) => {
  const errorMsgElement = document.getElementById('errorMsg');
  console.log("Error", error);

  if (errorMsgElement) {
    if (error) {
      errorMsgElement.innerHTML = error;
      if (error.cause) {
        errorMsgElement.innerHTML += ' ' + error.cause;
      }
    } else {
      errorMsgElement.innerHTML = 'Unknown error';
    }
  } else {
    throw new Error('Missing error message element');
  }
};

/**
 * It resets the error message element's text to an empty string
 */
const resetErrorMessageText = () => {
  const errorMsgElement = document.getElementById('errorMsg');
  if (errorMsgElement) {
    errorMsgElement.innerHTML = '';
  }
};

/**
 * It takes a new public key credential object and returns the attestation to
 * submit to PingOne MFA to register the device
 *
 * @param credential - The credential object returned by WebAuthn API's create
 * credential function
 * @returns An attestation object
 */
const buildAttestationForPingOneMFA = (credential) => {
  const newCredentialInfo = {};
  const response = {};

  if (credential.id) {
    newCredentialInfo.id = credential.id;
  }

  if (credential.type) {
    newCredentialInfo.type = credential.type;
  }

  if (credential.rawId) {
    newCredentialInfo.rawId = toBase64Str(credential.rawId);
  }

  if (!credential.response) {
    throw "Missing 'response' attribute in credential response";
  }

  response.clientDataJSON = toBase64Str(credential.response.clientDataJSON);

  response.attestationObject = toBase64Str(
    credential.response.attestationObject
  );

  newCredentialInfo.response = response;
  return newCredentialInfo;
};

/**
 * It takes the options object and converts it into a format that the
 * [create]{https://www.w3.org/TR/webauthn-2/#sctn-createCredential}
 * function from the WebAuthn API can understand
 *
 * @param options - The options object
 * @returns the publicKeyCreateCredentialsOptions object that the WebAuthn API
 * expects
 */
const buildPubKeyCredentialOptions = (options) => {
  const createCredentialsOptions = {};

  createCredentialsOptions.rp = options.rp;
  createCredentialsOptions.user = options.user;
  createCredentialsOptions.user.id = new Uint8Array(options.user.id);
  createCredentialsOptions.challenge = new Uint8Array(options.challenge);
  createCredentialsOptions.pubKeyCredParams = options.pubKeyCredParams;

  // Additional (optional) configuration
  if (options.timeout) {
    createCredentialsOptions.timeout = options.timeout;
  }

  if (options.excludeCredentials) {
    createCredentialsOptions.excludeCredentials = credentialListConversion(
      options.excludeCredentials
    );
  }

  if (options.authenticatorSelection) {
    createCredentialsOptions.authenticatorSelection =
      options.authenticatorSelection;
  }

  // Require [roaming or cross platform authenticators]{https://www.w3.org/TR/webauthn-2/#roaming-authenticators}
  createCredentialsOptions.authenticatorSelection.authenticatorAttachment =
    'cross-platform';

  if (options.attestation) {
    createCredentialsOptions.attestation = options.attestation;
  }

  if (options.extensions) {
    createCredentialsOptions.extensions = options.extensions;
  }

  return createCredentialsOptions;
};

/**
 * It creates a new WebAuthn credential and returns the information needed to
 * register it with PingOne MFA
 *
 * @param options - A JSON object that contains the options needed to create a
 * new credential using the WebAuthn API
 * @returns The attestation object is being returned
 */
const registerNewPubKeyCredAndSubmitAttestation = async (options) => {
  options = parseJSONIfNeeded(options);

  // Build the options object to pass in during the WebAuthn create call
  const publicKeyCredentialCreationOptions =
    buildPubKeyCredentialOptions(options);

  try {
    // Call WebAuthn's
    // [create]{https://www.w3.org/TR/webauthn-2/#sctn-createCredential}
    // method to register a new credential
    const pubKeyCred = await navigator.credentials.create({
      publicKey: publicKeyCredentialCreationOptions,
    });

    // Info needed for registering the device with PingOne MFA
    const attestedCred = buildAttestationForPingOneMFA(pubKeyCred);

    // Send new credential info to (PingOne MFA) server for verification and
    // registration
    const attestationElement = document.getElementById('attestation');
    const attestationBtn = document.getElementById('submitAttestationButton');
    // Updates the value in the element that'll have its value submitted
    attestationElement.value = JSON.stringify(attestedCred);
    // Submit the new credential info and advance the flow
    attestationBtn.click();

    return JSON.stringify(attestedCred);
  } catch (error) {
    const WEBAUTHN_FAILED_MSG =
      'Failed to use WebAuthn to create a new credential.';
    throw new Error(WEBAUTHN_FAILED_MSG, {
      cause: error,
    });
  }
};

/**
 * It takes the public key credential creation options from the flow variable,
 * creates a FIDO attestation, and returns the attestation
 *
 * @param event - The event object that is passed to the function.
 * @returns The attestation object is returned.
 */
const fidoRegistration = async (event) => {
  event?.preventDefault();
  resetErrorMessageText();

  const publicKeyCredentialCreationOptions =
    '{{global.variables.publicKeyCredentialCreationOptions}}';

  if (publicKeyCredentialCreationOptions) {
    try {
      return await registerNewPubKeyCredAndSubmitAttestation(
        publicKeyCredentialCreationOptions
      );
    } catch (error) {
      updateErrorMessageText(error);
      return false;
    }
  } else {
    // Missing Public Key Credential Creation Options
    updateErrorMessageText('Missing credential creation options');
  }
};

/**
 * Wait for page to load to start the FIDO registration process
 */
if (document.readyState === 'loading') {
  // Loading hasn't finished yet
  document.addEventListener('DOMContentLoaded', fidoRegistration);
} else {
  // `DOMContentLoaded` has already fired
  const fidoBtn = document.getElementById('fidoButton');
  if (fidoBtn) {
    fidoBtn.addEventListener('click', fidoRegistration);
  }

  return fidoRegistration();
}

/**
 *
 * WebAuthn API Check
 *
 * Check whether the WebAuthn API is available and submit the corresponding
 * boolean value to advance the flow.
 *
 */

/**
 * If the browser doesn't support WebAuthn, then set the value of the hidden
 * input field to false and submit the form. If the browser does support
 * WebAuthn, then set the value of the hidden input field to true and submit the
 * form
 *
 * @returns A boolean value representing WebAuthn API support.
 */
const webAuthnCheck = () => {
  if (!window.PublicKeyCredential) {
    document.getElementById('webAuthnSupport').value = false;
    document.getElementById('webAuthnSupportSubmitBtn').click();
    return false;
  }

  document.getElementById('webAuthnSupport').value = true;
  document.getElementById('webAuthnSupportSubmitBtn').click();
  return true;
};

/**
 * Wait for page to load to start the FIDO authentication process
 */
if (document.readyState === 'loading') {
  // Loading hasn't finished yet
  document.addEventListener('DOMContentLoaded', webAuthnCheck);
} else {
  // `DOMContentLoaded` has already fired
  const webAuthnSupportSubmitBtn = document.getElementById(
    'webAuthnSupportSubmitBtn'
  );
  if (webAuthnSupportSubmitBtn) {
    webAuthnSupportSubmitBtn.addEventListener('click', webAuthnCheck);
  }

  return webAuthnCheck();
}

