/**
 * Masks a phone number or email address.
 * @param  {Object} params - The parameters object containing an email or phone.
 * @returns {Object} An object with a maskedValue property containing the masked email or phone.
 */
module.exports = function maskContactInfo({ params: { email, phone } = {} }) {
    let maskedValue;

    if (email) {
        const [username, domain] = email.split("@");
        const maskedUsername =
            username.charAt(0) + "*".repeat(username.length - 2) + username.slice(-1);
        maskedValue = `${maskedUsername}@${domain}`;
    } else if (phone) {
        const numericPhoneNumber = phone.replace(/\D/g, '');
        maskedValue = `***-***-${numericPhoneNumber.slice(-4)}`;
    }

    return { maskedValue };
}

const url = require('url');
const pingOneHost = "auth.pingone.com";

module.exports = a = async ({ params }) => {
	const { skOpenId: { client_id, redirect_uri }, subflow, parameters, origin } = params
	const originUrl = origin ? url.parse(origin) : undefined;
	const redirectUrl = redirect_uri ? url.parse(redirect_uri) : undefined;
	const pingOneApplication = parameters?.application;

	if (subflow) {
		return { flowType: "SUBFLOW" };
	}

	if (client_id && client_id === "tryFlow") {
		return { flowType: "TRYFLOW" };
	}

	if (redirectUrl) {
		// Consider treating External IdP redirect separately in the future
		if (redirectUrl.host === pingOneHost) {
			return { flowType: "REDIRECT" };
		}
		return { flowType: "REDIRECT" };
	}

	if (pingOneApplication && originUrl.host === pingOneHost) {
		return { flowType: "TRIGGER" };
	}

	if (parameters) {
		if (origin) {
			return { flowType: "WIDGET" };
		}
		return { flowType: "API" };
	}

	return { 'testVariable': params };
}

