/**
     * Validates a phone number given a country code and phone number.
     * Ensures the country code starts with '+' and then validates
     * the combined phone number against an international phone number pattern.
     *
     * @param {Object} params - The input parameters containing countryCode and phoneNumber.
     * @param {string} params.countryCode - The country code, e.g., '1' or '+1'.
     * @param {string} params.phoneNumber - The phone number, e.g., '123-456-7890'.
     * @returns {boolean|undefined} - Returns false if the phone number is invalid; otherwise, undefined.
     */
module.exports = a = async ({ params }) => {
    let { countryCode, phoneNumber } = params;

    // Ensure the country code starts with '+'
    if (!countryCode.startsWith('+')) {
        countryCode = '+' + countryCode;
    }

    // Combine the country code and phone number into a full phone number
    var fullPhoneNumber = countryCode + phoneNumber;

    // Regular expression pattern for validating international phone numbers
    var phonePattern = /^\+\d{1,3}[-\s.]?\(?\d{1,4}\)?[-\s.]?\d{1,4}[-\s.]?\d{1,9}$/;

    // Test the full phone number against the pattern
    if (phonePattern.test(fullPhoneNumber)) {
        return; 
    } else {
        return false; 
    }
}

