:root {
  --animation-dimension: 120px;
  --animation-start-color: #71e171;
  --animation-midway-color: #67d067;
  --animation-finish-color: #59b259;
}

.checkmark {
  width: var(--animation-dimension);
  height: var(--animation-dimension);
  border-radius: 50%;
  display: block;
  stroke-width: 5;
  stroke: #ffffff;
  stroke-miterlimit: 10;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
  animation: fill 0.4s ease-in-out 0.4s forwards,
    scale 0.3s ease-in-out 0.9s both;
}

.checkmarkCircle {
  stroke-dasharray: 166;
  stroke-dashoffset: 166;
  stroke-width: 5;
  stroke-miterlimit: 10;
  stroke: var(--animation-finish-color);
  fill: none;
  animation: stroke 0.6s cubic-bezier(0.65, 0, 0.45, 1) forwards,
    colorChange 1.2s ease-in-out forwards;
}

.checkmarkCheck {
  transform-origin: 50% 50%;
  stroke-dasharray: 48;
  stroke-dashoffset: 48;
  animation: stroke 0.3s cubic-bezier(0.65, 0, 0.45, 1) 0.8s forwards,
    colorChange 0.6s ease-in-out 0.8s forwards;
}

@keyframes colorChange {
  0% {
    stroke: var(--animation-start-color);
  }
  50% {
    stroke: var(--animation-midway-color);
  } 
  100% {
    stroke: var(--animation-finish-color);
  }
}

@keyframes stroke {
  100% {
    stroke-dashoffset: 0;
  }
}
@keyframes scale {
  0%,
  100% {
    transform: none;
