8e014e14-d5db-457a-9205-c8dbc0ed9587.script:
"/*
 * MFA Enrollment Script
 *
 * Author  : Ping Identity  
 * Version : 1.1  
 * Date    : 03/28/2025  
 *
 * This script handles MFA device enrollment based on allowed MFA types and user selection.  
 * It may auto-enroll "EMAIL" as an MFA method if allowed.  
 *
 * Script Inputs  :  
 *    - "_id" (String) - User ID  
 *    - "ciam_allowedMFATypes" (Array) - List of allowed MFA methods  
 *    - "mfaDeviceType" (String) - Selected MFA device type for enrollment  
 *
 * Script Outputs :  
 *    - "errorMessage" (String) - User-friendly error message in case of failure  
 *    - "errorDetails" (String) - Detailed error message for debugging  
 *
 * Outcomes       :  
 *    - "true"  (MFA enrollment successful)  
 *    - "Error" (Unexpected execution failure)  
 *
 * Caution:  
 *    - This script may auto-enroll "EMAIL" as an MFA method if allowed.  
 *    - Admins managing WebAuthn should review FIDO2 registration behavior.  
 */

try {
    // Retrieve userId and allowedMFATypes from shared state
    var userId = nodeState.get("_id");
    var allowedMFAMethods = nodeState.get("ciam_allowedMFATypes");

    // Read the user's MFA devices from the directory
    var mfaDevicesObj = openidm.read("managed/alpha_user/" + userId, null, ["custom_mfaDevices"]);
    var mfaDevices = (mfaDevicesObj && mfaDevicesObj.custom_mfaDevices) ? mfaDevicesObj.custom_mfaDevices : [];

    // Parse the MFA devices into a usable array
    var usableMFADevices = [];
    mfaDevices.forEach(device => {
        usableMFADevices.push(device);
    });

    var emailMFAAllowed = false;

    // Check if EMAIL MFA is allowed
    allowedMFAMethods.forEach(method => {
        if (method === "EMAIL") emailMFAAllowed = true;
    });

    // Caution: By default, add EMAIL to the MFA devices list if it's allowed and not already present
    if (emailMFAAllowed && !usableMFADevices.includes("EMAIL")) {
        usableMFADevices.push("EMAIL");
    }

    // Get the selected device type from shared state
    var selectedDevice = nodeState.get("mfaDeviceType");

    // Handle device registration based on the selected device type
    // If Admin wants a single FIDO2 device that a user can register and authenticate with, 
    // change the node configuration in the WebAuthn Inner Journey and the below condition
    if (!usableMFADevices.includes(selectedDevice) || selectedDevice === "FIDO2") {
        // Handle combined MFA registration for OATH+PUSH if both are unregistered
        if (selectedDevice === "OATH+PUSH") {
            if(!usableMFADevices.includes("OATH") && !usableMFADevices.includes("PUSH")) {
                usableMFADevices.push("OATH");
                usableMFADevices.push("PUSH");
            } else {
                // OATH or PUSH device already registered for user, this option can't be use to register device again
                // If either of OATH or PUSH is registered and user want to register the other method from OATH/PUSH, 
                // Use separate OATH and PUSH paths for registration
                nodeState.putShared("errorMessage", "An unexpected error occurred, please contact support team.");
            }
        } else {
            usableMFADevices.push(selectedDevice);
        }
    }

    // Update the user's custom_mfaDevices attribute
    openidm.patch("managed/alpha_user/" + userId, null, [{"operation":"replace", "field":"/custom_mfaDevices", "value":usableMFADevices}]);

    // Update the custom_latestMFADevice variable to store the selected device, it will be used in the notification template
    var latestMFADevice = selectedDevice === "OATH+PUSH" ? "OATH & Push" : selectedDevice;
    openidm.patch("managed/alpha_user/" + userId, null, [{"operation":"replace", "field":"/custom_latestMFADevice", "value":latestMFADevice}]);

    // Set the outcome to true
    action.goTo("true");   
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");

    // Detailed error information for debugging
    nodeState.putShared("errorDetails", e.toString());

    // Go to Error  
    action.goTo("Error");
}"

6ce5e10e-76a0-4172-b0cd-c2dcaeb7ef92.script:
"/*
 * One-Time Password (OTP) Input Validation Script
 *
 * Author  : Ping Identity  
 * Version : 1.1 
 * Date    : 03/28/2025  
 *
 * This script enforces input validation rules on OTP fields and ensures that only valid codes can be submitted.
 * It dynamically applies constraints based on OTP length and handles UI updates.
 *
 * Script Inputs  :  
 *    - "oneTimePassword" (String) - OTP generated by a prior node (HOTP Generator, Twilio, etc.).  
 *    - "invalidCodeErrorMessage" (String) - Error message displayed when an incorrect OTP is entered.  
 *
 * Script Outputs :  
 *    - "errorMessage" (String) - User-friendly error message in case of failure  
 *    - "errorDetails" (String) - Detailed error message for debugging  
 *    - "invalidCodeErrorMessage" (String) - Cleared out to reset error states  
 *
 * Outcomes       :  
 *    - "true"  (Validation script injected successfully)  
 *    - "Error" (Unexpected execution failure)  
 *
 * Note:  
 *    - If HOTP Generator node is used prior, "oneTimePassword" will be a transient state property,  
 *      make sure to make it available in script inputs.  
 *    - If the OTP is generated by Twilio nodes, the default length is set to 6 and only digits are considered.  
 *      Modify this if necessary.  
 */

try {
    /**
     * Returns a JavaScript string that:
     *  - Sets the max length for OTP input fields.
     *  - Enforces numeric-only input using pattern matching.
     *  - Disables the submit button until a valid OTP is entered.
     */
    function validateInput(otpLength) {
        return String(" \n\
                    var otpLength = " + otpLength + " \n\
                    const submitBtn = document.querySelectorAll('button[type=\"submit\"]')[0]; \n\
                    if (submitBtn) submitBtn.disabled = true; \n\
                    // Validate the passcode \n\
                    function validateCode() { \n\
                        const passcode = document.querySelectorAll('input[data-vv-as=\"One Time Password\"]')[0] || document.querySelectorAll('input[data-vv-as=\"Code\"]')[0]; \n\
                        if (passcode) { \n\
                            // Set input properties to prevent user tampering \n\
                            passcode.setAttribute('maxlength', otpLength); \n\
                            passcode.setAttribute('pattern', '[0-9]{' + otpLength + '}'); \n\
                            passcode.setAttribute('inputmode', 'numeric'); \n\
                            // Check if the input is exactly 6 digits long and numeric \n\
                            const isValidCode = new RegExp('^[0-9]{' + otpLength + '}$').test(passcode.value); \n\
                            submitBtn.disabled = !isValidCode; \n\
                            return isValidCode; \n\
                        } \n\
                        return false; \n\
                    }; \n\
                    \n\
                    function go(obs) { \n\
                        const passcode = document.querySelectorAll('input[data-vv-as=\"One Time Password\"]')[0] || document.querySelectorAll('input[data-vv-as=\"Code\"]')[0]; \n\
                        if (passcode && submitBtn) { \n\
                            passcode.addEventListener('input', validateCode); \n\
                            submitBtn.addEventListener('click', (event) => { \n\
                                if (!validateCode()) { \n\
                                    event.preventDefault(); \n\
                                    submitBtn.disabled = true; \n\
                                } \n\
                            }); \n\
                            if (obs) obs.disconnect(); \n\
                            return; \n\
                        } \n\
                    } \n\
                    \n\
                    // For HOTP and Twilio OTP collector nodes, if either of the element is present, initialize validation \n\
                    if (document.querySelectorAll('input[data-vv-as=\"One Time Password\"]')[0] || document.querySelectorAll('input[data-vv-as=\"Code\"]')[0]) go(); \n\
                    else { \n\
                        const observer = new MutationObserver((mutations, obs) => { go(obs); }); \n\
                        observer.observe(document, { childList: true, subtree: true }); \n\
                    } \n\
        ");
    }

    // Retrieve error message if the user entered an invalid OTP previously
    var invalidCodeErrorMessage = nodeState.get("invalidCodeErrorMessage");

    // Reset the error message for the next attempt
    nodeState.putShared("invalidCodeErrorMessage", "");

    // Get "oneTimePassword" shared in transient state by the HOTP Generator node or default it to 6
    var otpLength = nodeState.get("oneTimePassword") ? nodeState.get("oneTimePassword").length : 6;

    if (callbacks.isEmpty()) {
        // Display error message if the previous OTP attempt was invalid
        if (invalidCodeErrorMessage && invalidCodeErrorMessage.length) {
            callbacksBuilder.textOutputCallback(2, invalidCodeErrorMessage);
        }

        // Inject validation script into the UI
        callbacksBuilder.scriptTextOutputCallback(validateInput(otpLength));
    }

    // Proceed to success path
    action.goTo("true");
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");

    // Detailed error information for debugging
    nodeState.putShared("errorDetails", e.toString());

    // Go to Error  
    action.goTo("Error");
}"

c3d313f3-a85c-40dc-aac9-1ba98a033cab.script:
"/*
 * MFA Device Selection Script
 * 
 * Author  : Ping Identity  
 * Version : 1.1 
 * Date    : 03/28/2025  
 * 
 * Determines the MFA device type to register based on available options and user selection.
 * 
 * Script Inputs:
 *    - "ciam_allowedMFATypes" (Array) - Permitted MFA methods.
 * 
 * Script Outputs:
 *    - "mfaDeviceType" (String) - Selected MFA device.
 *    - "errorMessage"  (String) - User-friendly error message if an error occurs.
 *    - "errorDetails"  (String) - Detailed error message for debugging.
 * 
 * Outcomes:
 *    - "true"  (Success)
 *    - "Error" (Failure)
 */

try {
    // Retrieve the list of allowed MFA methods from shared state
    var allowedMFAMethods = nodeState.get("ciam_allowedMFATypes");

    // Initialize an array to store MFA methods that can be used
    var usableMFAMethods = [];

    // Populate the usable MFA methods array
    allowedMFAMethods.forEach(device => {
        usableMFAMethods.push(device);
    });

    // Remove "EMAIL" from the list as it is verified in the parent journey
    if (usableMFAMethods.includes("EMAIL")) {
        var index = usableMFAMethods.indexOf("EMAIL");
        if (index > -1) usableMFAMethods.splice(index, 1);
    }

    // Add "OATH+PUSH" for combined registration if both OATH and Push methods are available
    if (usableMFAMethods.includes("OATH") && usableMFAMethods.includes("PUSH")) {
        usableMFAMethods.push("OATH+PUSH");
    }

    if (usableMFAMethods.length === 1) {
        // If only one MFA method is available, auto-select it
        var selectedDevice = usableMFAMethods[0] || null;

        if (!selectedDevice) {
            // Handle unexpected errors
            nodeState.putShared("errorMessage", "An unexpected error occurred. Please contact support.");
            action.goTo("Error");
        } else {
            nodeState.putShared("mfaDeviceType", selectedDevice);
            action.goTo(selectedDevice);
        }
    } else {
        // Handle user selection when multiple MFA options exist
        if (callbacks.isEmpty()) {
            // Display MFA device selection prompt
            callbacksBuilder.choiceCallback("Select an MFA device", usableMFAMethods, 0, false);
        } else {
            // Process user's selected MFA device
            var index = callbacks.getChoiceCallbacks().get(0)[0];
            var selectedDevice = usableMFAMethods[index] || null;

            if (!selectedDevice) {
                nodeState.putShared("errorMessage", "An unexpected error occurred. Please contact support.");
                action.goTo("Error");
            } else {
                nodeState.putShared("mfaDeviceType", selectedDevice);
                action.goTo(selectedDevice);
            }
        }
    }
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");

    // Detailed error information for debugging
    nodeState.putShared("errorDetails", e.toString());

    // Go to Error
    action.goTo("Error");
}"

