97707ce0-3974-4ee5-8cef-6de83bf0e757.script:
"/*
 * Script: Protect Risk Evaluation Processing
 * 
 * Author  : Ping Identity  
 * Version : 1.1  
 * Date    : 03/28/2025
 * 
 * This script processes risk evaluation data from PingOne Protect and extracts key details such as 
 * city, state, and device OS and stores in user objectAttributes
 *
 * Script Inputs:
 *    - "objectAttributes" (Object) - Existing user attributes to update.
 *    - "PingOneProtectEvaluationNode.RISK" (Object) - Risk evaluation response from PingOne Protect.
 *
 * Script Outputs:
 *    - "objectAttributes" (Object) - Updated attributes including protectActivityCity, protectActivityState, and protectDeviceOS.
 *    - "errorMessage" (String) - User-friendly error message if an error occurs.
 *    - "errorDetails" (String) - Detailed error message for debugging.
 *
 * Outcomes:
 *    - "true"  (Script executed successfully).
 *    - "Error" (Unexpected failure).
 */

try {
    /**
     * Capitalizes the first letter of each word in a given string.
     * Example: "new york" -> "New York"
     * @param {string} string - The input string to transform.
     * @returns {string} - The transformed string with capitalized words.
     */
    function capitalizeFirstLetter(string) {
        return string
        .split(' ')                   // Split the string into an array of words
        .map(word => 
            word.charAt(0).toUpperCase() + word.slice(1)
        )                             // Capitalize the first letter of each word
        .join(' ');                   // Join the array back into a single string
    }

    // Retrieve existing object attributes from node state
    var attributes = nodeState.get("objectAttributes");

    // Retrieve risk evaluation response from PingOne Protect
    var rawResponse = nodeState.get("PingOneProtectEvaluationNode.RISK");

    // Extract key details from the risk response
    var protectActivityCity = rawResponse.details.city;
    var protectActivityState = rawResponse.details.state;
    var protectDeviceOS = rawResponse.details.device.os.name;

    // Capitalize city and state names for consistency
    protectActivityCity = capitalizeFirstLetter(protectActivityCity);
    protectActivityState = capitalizeFirstLetter(protectActivityState);

    // Update attributes if they exist
    if (attributes) {
        attributes.put("custom_protectActivityCity", protectActivityCity);
        attributes.put("custom_protectActivityState", protectActivityState);
        attributes.put("custom_protectDeviceOS", protectDeviceOS);

        // Store updated attributes in the shared state
        nodeState.putShared("objectAttributes", attributes);
    }

    // Proceed to the success path
    action.goTo("true");
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");

    // Detailed error information for debugging
    nodeState.putShared("errorDetails", e.toString());

    // Go to Error  
    action.goTo("Error");
}"

