"/*
 * Extract AIID Cookie Script
 *
 * Author  : Ping Identity  
 * Version : 1.0  
 * Date    : 03/28/2025 
 *
 * Script Inputs  :  
 *    - "Cookie" (String) - Retrieved from request headers  
 *
 * Script Outputs :  
 *    - "pingOneApplicationInstanceId" (String) - Extracted AIID cookie value  
 *    - "errorMessage" (String) - User-friendly error message in case of failure  
 *    - "errorDetails" (String) - Detailed error message for debugging  
 *
 * Outcomes       :  
 *    - "true"  (Successfully extracted the cookie value)  
 *    - "Error" (An error occurred during execution)  
 */

try {
    // Get the cookie name you want to extract
    var cookieName = "_aiid";  // Change this to the actual cookie name

    // Retrieve cookies from the request headers
    var cookies = requestHeaders.get("Cookie");

    if (cookies) {
        // Convert cookies string to key-value pairs
        var cookieArray = cookies[0].split("; ");
        var cookieMap = {};

        for (var i = 0; i < cookieArray.length; i++) {
            var parts = cookieArray[i].split("=");
            cookieMap[parts[0]] = parts[1];
        }

        // Extract the required cookie value
        if (cookieMap[cookieName]) {
            nodeState.putShared("pingOneApplicationInstanceId", cookieMap[cookieName]);
            outcome = "true";
        } 
    } 
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");
    // Detailed error information for debugging
    nodeState.putShared("errorDetails", e.toString());
