e1781e83-6188-4647-ac44-95718fe949b6.script:
"/* OTP Email Sender
 *
 * Author: Ping Identity
 * Version: 1.0
 * Date: 03/28/2025
 * 
 * This script sends an email containing a one-time password (HOTP) to a user's email address.
 * It is used within a journey that requires sending an OTP for verification or authentication purposes.
 * 
 * This script does not need to be parametrized. It will work properly as is, assuming the necessary inputs are available in the shared or node state.
 * 
 * Script Inputs:
 *    - "_id" (String) - User ID. Retrieved from nodeState.
 *    - "oneTimePassword" (String) - The generated OTP to be sent to the user. Retrieved from nodeState.
 *
 * Outcomes:
 *    - "sent" (PASS) - Email successfully sent.
 *    - "noMail" (FAIL) - No email address found, email not sent.
 *    - "error" (ERROR) - An error occurred while processing the email sending.
 *
 * Notes:
 *    - The script uses the IDM Email Service openidm binding for sending templated emails.
 *    - Ensure the necessary attributes are available in sharedState or nodeState for successful execution.
 */
/**
 * Script configuration
*/
var config = {
    /**
     * @property {string} templateID - Identifier of email template
     * @property {string} idmEndpoint - IDM Endpoint used to send templated emails
     * @property {string} idmAction - IDM action executed against the idmEndpoint
     */
    templateID: "otp",
    idmEndpoint: "external/email",
    idmAction: "sendTemplate"
};
/**
 * Node outcomes
*/
var nodeOutcome = {
    PASS: "sent",
    FAIL: "noMail",
    ERROR: "error"
};
/**
 * Send email via the IDM Email Service openidm binding in next-gen scripting
 *
 * @param {string} hotp - HOTP retrieved from transientState
 * @param {string} mail - mail attribute retrieved from the idRepository. Note if this is a registration journey acquire mail from sharedState
 * @param {string} givenName - givenName attribute retrieved from the idRepository. Note if this is a registration journey acquire givenName from sharedState
 */
function sendMail(hotp, mail, givenName) {
    try {
        var params = new Object();
        params.templateName = config.templateID;
        params.to = mail;
        params.object = {
            "givenName": givenName,
            "otp": hotp
        };
        openidm.action(config.idmEndpoint, config.idmAction, params);
        //Email send successfully
        return nodeOutcome.PASS;
    }
    catch (e) {
       // Failed to call IDM Email endpoint using template.
        return nodeOutcome.ERROR;
    }
};
/**
 * Main function
 */
(function () {
   // Node execution started
    var id;
    var hotp;
    var mail;
    if (!(id = nodeState.get("_id"))) {
       // Unable to retrieve Identity
        action.goTo(nodeOutcome.ERROR);
        return;
    } else {
        var identity = idRepository.getIdentity(id);
    }
    if (!(hotp = nodeState.get("oneTimePassword"))) {
       // Unable to retrieve HOTP from state
        action.goTo(nodeOutcome.ERROR);
        return;
    }
    //If this is a registration journey adapt the following to try retrieve from nodeState
    if (!(mail = identity.getAttributeValues("mail").toArray()[0])) {
     //   Unable to retrieve mail attribute from the idRepository
        action.goTo(nodeOutcome.FAIL);
        return;
    }
    //If this is a registration journey adapt the following to try retrieve from nodeState
    //Be sure to set givenName against the user
    if (!(givenName = identity.getAttributeValues("givenName").toArray()[0])) {
      // Unable to retrieve givenName attribute from the idRepository
        action.goTo(nodeOutcome.FAIL);
        return;
    }
    //Execute function to send mail
    outcome = sendMail(hotp, mail, givenName);
})();"

be50351b-c077-4827-b9aa-3a61a08b30f9.script:
"/*
 * Recovery Code Input Validation Script
 *
 * Author  : Ping Identity  
 * Version : 1.1  
 * Date    : 03/28/2025  
 *
 * This script ensures that the recovery code input follows a strict 10-character requirement.
 * It prevents form submission until a valid code is entered and dynamically enables/disables the submit button.
 *
 * Script Inputs  :  
 *    - "invalidCodeErrorMessage" (String) - Error message displayed when an incorrect recovery code is entered.  
 *
 * Script Outputs :  
 *    - "errorMessage" (String) - User-friendly error message in case of failure.  
 *    - "errorDetails" (String) - Detailed error message for debugging.  
 *    - "invalidCodeErrorMessage" (String) - Cleared out to reset error states.  
 *
 * Outcomes       :  
 *    - "true"  (Validation script injected successfully).  
 *    - "Error" (Unexpected execution failure).  
 */

try {
    /**
     * Returns a JavaScript string that:
     * - Ensures the recovery code is exactly 10 characters long.
     * - Disables the submit button until a valid input is entered.
     * - Observes DOM changes and initializes validation dynamically.
     */
    function validateInput() {
        return String(" \n\
                    const submitBtn = document.querySelectorAll('button[type=\"submit\"]')[0]; \n\
                    if(submitBtn) submitBtn.disabled = true; \n\
                    function go(obs) { \n\
                        const recoveryCode = document.querySelectorAll('input[data-vv-as=\"Recovery Code\"]')[0]; \n\
                        if (recoveryCode && submitBtn) { \n\
                            // Set the maxlength attribute to 10 \n\
                            recoveryCode.setAttribute('maxlength', 10); \n\
                            const enableSubmitBtn = () => { \n\
                                // Check input validity before enabling submit button \n\
                                const isValidInput = recoveryCode.value.length === 10; \n\
                                if (isValidInput) { \n\
                                    submitBtn.disabled = false; \n\
                                } else { \n\
                                    submitBtn.disabled = true; \n\
                                } \n\
                            }; \n\
                            // Revalidate before form submission \n\
                            submitBtn.addEventListener('click', (event) => { \n\
                                const isValidInput = recoveryCode.value.length === 10; \n\
                                if (!isValidInput) { \n\
                                    event.preventDefault(); \n\
                                    submitBtn.disabled = true; \n\
                                } \n\
                            }); \n\
                            recoveryCode.addEventListener('input', enableSubmitBtn); \n\
                            if (obs) obs.disconnect(); \n\
                            return; \n\
                        } \n\
                    } \n\
                    \n\
                    // If the recovery code field exists, initialize validation. Otherwise, observe DOM changes. \n\
                    if (document.querySelectorAll('input[data-vv-as=\"Recovery Code\"]')[0]) go(); \n\
                    else { \n\
                        const observer = new MutationObserver((mutations, obs) => { go(obs); }); \n\
                        observer.observe(document, { childList: true, subtree: true }); \n\
                    } \n\
        ");
    }

    // Retrieve any previous invalid code error message
    var invalidCodeErrorMessage = nodeState.get("invalidCodeErrorMessage");

    // Clear the invalidCodeErrorMessage to reset the error state
    nodeState.putShared("invalidCodeErrorMessage", "");

    if (callbacks.isEmpty()) {                
        // Display an error message if the previous recovery code attempt was invalid
        if (invalidCodeErrorMessage && invalidCodeErrorMessage.length) {
            callbacksBuilder.textOutputCallback(2, invalidCodeErrorMessage);
        }

        // Inject validation script into the UI
        callbacksBuilder.scriptTextOutputCallback(validateInput());
    }

    // Proceed to the success path
    action.goTo("true");
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");

    // Detailed error information for debugging
    nodeState.putShared("errorDetails", e.toString());

    // Go to Error  
    action.goTo("Error");
}"

6291a5b7-a262-4127-a5df-8ea238da4796.script:
"/*
 * MFA Device Selection Script
 *
 * Author  : Ping Identity  
 * Version : 1.1  
 * Date    : 03/28/2025  
 *
 * This script determines which MFA devices are available to the user based on their profile and administrative policy.
 * It builds a list of usable MFA devices (filtered by policy), retrieves the latest registered option, and injects
 * a choice callback into the UI. If Magic Link authentication is enabled, it is added as an additional option.
 *
 * Script Inputs  :  
 *    - "_id" (String) - The unique identifier of the user.  
 *    - "ciam_allowedMFATypes" (Array) - List of MFA methods allowed by admin policy.  
 *    - "ciam_magicLinkEnabled" (Boolean) - Indicates if Magic Link authentication is permitted.  
 *
 * Script Outputs :  
 *    - "usableMFADevices" (Array) - Filtered list of MFA methods the user is eligible to use.  
 *    - "errorMessage" (String) - User-friendly error message in case of failure.  
 *    - "errorDetails" (String) - Detailed error message for debugging.  
 *
 * Outcomes       :  
 *    - "true"  (An MFA device has been selected and validated).  
 *    - "Error" (Unexpected execution failure or no usable MFA device available).  
 */

try {
    // Retrieve user ID and allowed MFA devices
    var userId = nodeState.get("_id");
    var allowedMFAMethods = nodeState.get("ciam_allowedMFATypes");

    var mfaMapping = {
        "EMAIL": "allowEmail",
        "SMS": "allowSMS",
        "VOICE": "allowVoice",
        "OATH": "allowOATH",
        "PUSH": "allowPush",
        "FIDO2": "allowFIDO2"
    };

    var allowEmail = false, allowSMS = false, allowVoice = false, allowOATH = false, allowPush = false, allowFIDO2 = false;

    allowedMFAMethods.forEach(device => {
        if (mfaMapping[device]) {
            this[mfaMapping[device]] = true;
        }
    });

    var mfaDevicesObj = openidm.read("managed/alpha_user/" + userId, null, ["custom_mfaDevices"]);
    var mfaDevices = (mfaDevicesObj && mfaDevicesObj.custom_mfaDevices) ? mfaDevicesObj.custom_mfaDevices : [];

    // By default returned mfaDevices is of Object type so values are parsed in a new array
    var usableMFADevices = [];

    // Store MFA devices and additional option in shared state    
    mfaDevices.forEach(device => {
        usableMFADevices.push(device);
    });

    usableMFADevices = usableMFADevices.filter(device =>
        (device === "SMS" && allowSMS) ||
        (device === "EMAIL" && allowEmail) ||
        (device === "VOICE" && allowVoice) ||
        (device === "OATH" && allowOATH) ||
        (device === "PUSH" && allowPush) ||
        (device === "FIDO2" && allowFIDO2)                               
    );

    // Index of latest registered MFA device
    var idxLatestMFADevice = usableMFADevices ? usableMFADevices.length - 1 : -1;

    // Add Magic Link, if enabled by Admin
    var magicLinkEnabled = nodeState.get("ciam_magicLinkEnabled");
    if(magicLinkEnabled) usableMFADevices.push("Magic Link");

    // Handle callback logic
    if (callbacks.isEmpty()) {
      callbacksBuilder.choiceCallback("Select an MFA device", usableMFADevices, idxLatestMFADevice, false);
    } else {
      // Callbacks returned from browser, save selected choice
      var index = callbacks.getChoiceCallbacks().get(0)[0];
      var selectedDevice = usableMFADevices[index] || null;

      if (!selectedDevice) {
        nodeState.putShared("errorMessage", "No registered MFA device found");
        // Go to error  
        action.goTo("Error");
      } else {
        // Go to authentication for that selected MFA device
        action.goTo(selectedDevice);
      }
    }
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");
    // Detailed error information for debugging
    nodeState.putShared("errorDetails", e.toString());
    // Go to Error  
    action.goTo("Error");
}"

6ce5e10e-76a0-4172-b0cd-c2dcaeb7ef92.script:
"/*
 * One-Time Password (OTP) Input Validation Script
 *
 * Author  : Ping Identity  
 * Version : 1.1 
 * Date    : 03/28/2025  
 *
 * This script enforces input validation rules on OTP fields and ensures that only valid codes can be submitted.
 * It dynamically applies constraints based on OTP length and handles UI updates.
 *
 * Script Inputs  :  
 *    - "oneTimePassword" (String) - OTP generated by a prior node (HOTP Generator, Twilio, etc.).  
 *    - "invalidCodeErrorMessage" (String) - Error message displayed when an incorrect OTP is entered.  
 *
 * Script Outputs :  
 *    - "errorMessage" (String) - User-friendly error message in case of failure  
 *    - "errorDetails" (String) - Detailed error message for debugging  
 *    - "invalidCodeErrorMessage" (String) - Cleared out to reset error states  
 *
 * Outcomes       :  
 *    - "true"  (Validation script injected successfully)  
 *    - "Error" (Unexpected execution failure)  
 *
 * Note:  
 *    - If HOTP Generator node is used prior, "oneTimePassword" will be a transient state property,  
 *      make sure to make it available in script inputs.  
 *    - If the OTP is generated by Twilio nodes, the default length is set to 6 and only digits are considered.  
 *      Modify this if necessary.  
 */

try {
    /**
     * Returns a JavaScript string that:
     *  - Sets the max length for OTP input fields.
     *  - Enforces numeric-only input using pattern matching.
     *  - Disables the submit button until a valid OTP is entered.
     */
    function validateInput(otpLength) {
        return String(" \n\
                    var otpLength = " + otpLength + " \n\
                    const submitBtn = document.querySelectorAll('button[type=\"submit\"]')[0]; \n\
                    if (submitBtn) submitBtn.disabled = true; \n\
                    // Validate the passcode \n\
                    function validateCode() { \n\
                        const passcode = document.querySelectorAll('input[data-vv-as=\"One Time Password\"]')[0] || document.querySelectorAll('input[data-vv-as=\"Code\"]')[0]; \n\
                        if (passcode) { \n\
                            // Set input properties to prevent user tampering \n\
                            passcode.setAttribute('maxlength', otpLength); \n\
                            passcode.setAttribute('pattern', '[0-9]{' + otpLength + '}'); \n\
                            passcode.setAttribute('inputmode', 'numeric'); \n\
                            // Check if the input is exactly 6 digits long and numeric \n\
                            const isValidCode = new RegExp('^[0-9]{' + otpLength + '}$').test(passcode.value); \n\
                            submitBtn.disabled = !isValidCode; \n\
                            return isValidCode; \n\
                        } \n\
                        return false; \n\
                    }; \n\
                    \n\
                    function go(obs) { \n\
                        const passcode = document.querySelectorAll('input[data-vv-as=\"One Time Password\"]')[0] || document.querySelectorAll('input[data-vv-as=\"Code\"]')[0]; \n\
                        if (passcode && submitBtn) { \n\
                            passcode.addEventListener('input', validateCode); \n\
                            submitBtn.addEventListener('click', (event) => { \n\
                                if (!validateCode()) { \n\
                                    event.preventDefault(); \n\
                                    submitBtn.disabled = true; \n\
                                } \n\
                            }); \n\
                            if (obs) obs.disconnect(); \n\
                            return; \n\
                        } \n\
                    } \n\
                    \n\
                    // For HOTP and Twilio OTP collector nodes, if either of the element is present, initialize validation \n\
                    if (document.querySelectorAll('input[data-vv-as=\"One Time Password\"]')[0] || document.querySelectorAll('input[data-vv-as=\"Code\"]')[0]) go(); \n\
                    else { \n\
                        const observer = new MutationObserver((mutations, obs) => { go(obs); }); \n\
                        observer.observe(document, { childList: true, subtree: true }); \n\
                    } \n\
        ");
    }

    // Retrieve error message if the user entered an invalid OTP previously
    var invalidCodeErrorMessage = nodeState.get("invalidCodeErrorMessage");

    // Reset the error message for the next attempt
    nodeState.putShared("invalidCodeErrorMessage", "");

    // Get "oneTimePassword" shared in transient state by the HOTP Generator node or default it to 6
    var otpLength = nodeState.get("oneTimePassword") ? nodeState.get("oneTimePassword").length : 6;

    if (callbacks.isEmpty()) {
        // Display error message if the previous OTP attempt was invalid
        if (invalidCodeErrorMessage && invalidCodeErrorMessage.length) {
            callbacksBuilder.textOutputCallback(2, invalidCodeErrorMessage);
        }

        // Inject validation script into the UI
        callbacksBuilder.scriptTextOutputCallback(validateInput(otpLength));
    }

    // Proceed to success path
    action.goTo("true");
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");

    // Detailed error information for debugging
    nodeState.putShared("errorDetails", e.toString());

    // Go to Error  
    action.goTo("Error");
}"

07e3c04a-1cf0-42e9-ab80-c9c06634c530.script:
"/*
 * Verification Code Validation Script
 *
 * Author  : Ping Identity  
 * Version : 1.1  
 * Date    : 03/28/2025  
 *
 * This script ensures that the user enters a valid 6-digit verification code before enabling the submit button.
 * It prevents premature form submission and enforces numeric input constraints.
 *
 * Script Inputs:
 *    - "invalidCodeErrorMessage" (String) - Error message displayed when an incorrect verification code is entered.
 *
 * Script Outputs:
 *    - "errorMessage" (String) - User-friendly error message if an error occurs.
 *    - "errorDetails" (String) - Detailed error message for debugging.
 *    - "invalidCodeErrorMessage" (String) - Cleared out after validation to reset error states.
 *
 * Outcomes:
 *    - "true"  (Script executed successfully).
 *    - "Error" (Unexpected failure).
 */

try {
    /**
     * Returns a JavaScript string that:
     * - Ensures the verification code is exactly 6 digits long.
     * - Disables the submit button until a valid input is entered.
     * - Prevents form submission if the input is invalid.
     * - Observes DOM changes and initializes validation dynamically.
     */
    function validateInput() {
        return String(" \n\
                    // Validate the verification code \n\
                    function validateCode() { \n\
                        const submitBtn = document.querySelectorAll('button[type=\"button\"]')[0]; \n\
                        const verificationCode = document.querySelectorAll('input[data-vv-as=\"Enter verification code\"]')[0]; \n\
                        if (verificationCode) { \n\
                            // Restrict input to 6-digit numeric values \n\
                            verificationCode.setAttribute('maxlength', '6'); \n\
                            verificationCode.setAttribute('pattern', '[0-9]{6}'); \n\
                            verificationCode.setAttribute('inputmode', 'numeric'); \n\
                            \n\
                            // Validate the input format \n\
                            const isValidCode = /^[0-9]{6}$/.test(verificationCode.value); \n\
                            submitBtn.disabled = !isValidCode; \n\
                            return isValidCode; \n\
                        } \n\
                        return false; \n\
                    }; \n\
                    \n\
                    // Prevent form submission via Enter key if input is invalid \n\
                    function preventEnterKeyDefault(event) { \n\
                        const submitBtn = document.querySelectorAll('button[type=\"button\"]')[0]; \n\
                        if (event.key === 'Enter' && !validateCode()) { \n\
                            event.preventDefault(); \n\
                            if (submitBtn) submitBtn.disabled = true; \n\
                        } \n\
                    } \n\
                    \n\
                    function go(obs) { \n\
                        // Auto-submit and navigate to 'Not Registered' if the method is not registered for the user \n\
                        const nextBtn = document.querySelectorAll('button[type=\"submit\"]')[0]; \n\
                        if (nextBtn) { \n\
                            nextBtn.style.visibility='hidden'; \n\
                            nextBtn.click(); \n\
                        } \n\
                        \n\
                        const submitBtn = document.querySelectorAll('button[type=\"button\"]')[0]; \n\
                        if (submitBtn) submitBtn.disabled = true; \n\
                        \n\
                        const verificationCode = document.querySelectorAll('input[data-vv-as=\"Enter verification code\"]')[0]; \n\
                        if (verificationCode && submitBtn) { \n\
                            // Attach event listeners to handle validation dynamically \n\
                            verificationCode.addEventListener('input', validateCode); \n\
                            verificationCode.addEventListener('keypress', preventEnterKeyDefault); \n\
                            \n\
                            // Prevent form submission if input is invalid \n\
                            submitBtn.addEventListener('click', (event) => { \n\
                                if (!validateCode()) { \n\
                                    event.preventDefault(); \n\
                                    submitBtn.disabled = true; \n\
                                } \n\
                            }); \n\
                            \n\
                            if (obs) obs.disconnect(); \n\
                            return; \n\
                        } \n\
                    } \n\
                    \n\
                    // If both elements are present, initialize validation \n\
                    if (document.querySelectorAll('button[type=\"button\"]')[0] && document.querySelectorAll('input[data-vv-as=\"Enter verification code\"]')[0]) go(); \n\
                    else { \n\
                        const observer = new MutationObserver((mutations, obs) => { go(obs); }); \n\
                        observer.observe(document, { childList: true, subtree: true }); \n\
                    } \n\
        ");
    }

    // Retrieve any previous invalid code error message
    var invalidCodeErrorMessage = nodeState.get("invalidCodeErrorMessage");

    // Clear the invalidCodeErrorMessage to reset the error state
    nodeState.putShared("invalidCodeErrorMessage", "");

    if (callbacks.isEmpty()) {
        // Display an error message if the previous verification code attempt was invalid
        if (invalidCodeErrorMessage && invalidCodeErrorMessage.length) {
            callbacksBuilder.textOutputCallback(2, invalidCodeErrorMessage);
        }

        // Inject validation script into the UI
        callbacksBuilder.scriptTextOutputCallback(validateInput());
    }

    // Proceed to the success path
    action.goTo("true");
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");

    // Detailed error information for debugging
    nodeState.putShared("errorDetails", e.toString());

    // Go to Error  
    action.goTo("Error");
}"

