"/*
 * MFA Type Restriction and Restoration Script
 *
 * Author  : Ping Identity  
 * Version : 1.0  
 * Date    : 03/28/2025  
 *
 * This script temporarily restricts allowed MFA methods to SMS and restores the original configuration if it exists.
 *
 * Script Inputs  : "
 *    - "ciam_allowedMFATypes" (Array) - List of allowed MFA methods from shared state  
 *    - "ciam_originalAllowedMFATypes" (Array) - Backup of the original allowed MFA methods 
 *
 * Script Outputs :  
 *    - "ciam_allowedMFATypes" (Array) - List of allowed MFA methods after modification  
 *    - "ciam_originalAllowedMFATypes" (Array) - Backup of the original allowed MFA methods  
 *    - "errorMessage" (String) - User-friendly error message in case of failure  
 *    - "errorDetails" (String) - Detailed error message for debugging  
 *
 * Outcomes       :  
 *    - "true"  (MFA methods successfully updated)  
 *    - "Error" (Unexpected execution failure)  
 */

try {
    // Check if the original allowed MFA types are already stored
    var originalAllowedMFAMethods = nodeState.get("ciam_originalAllowedMFATypes");

    if (originalAllowedMFAMethods) {
        // Restore the original MFA methods, excluding SMS if not originally present
        var usableMFAMethods = [];

        originalAllowedMFAMethods.forEach(device => {
            if (device !== "SMS") usableMFAMethods.push(device);
        });

        nodeState.putShared("ciam_allowedMFATypes", usableMFAMethods);
    } else {
        // Retrieve the current allowed MFA methods
        var allowedMFAMethods = nodeState.get("ciam_allowedMFATypes");

        // Restrict allowed MFA methods to SMS for direct registration
        allowedMFAMethods.forEach(device => {
            if (device === "SMS") nodeState.putShared("ciam_allowedMFATypes", ["SMS"]);
        });

        // Store the original MFA methods for future restoration
        nodeState.putShared("ciam_originalAllowedMFATypes", allowedMFAMethods);
    }

    // Proceed to success path
    action.goTo("true");
} catch (e) {
    // Store a user-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Please contact support.");

    // Log the detailed error message for debugging
    nodeState.putShared("errorDetails", e.toString());

    // Redirect to the error handling node
    action.goTo("Error");
}"

