3e759329-e6d7-4c5b-970d-8f0d6d874533.script:
"/*
 * Process Captured Govt. Id Data
 *
 * Author  : Ping Identity  
 * Version : 1.0  
 * Date    : 03/28/2025  
 *
 * This script validates biographic attributes extracted from a government-issued ID 
 * and updates user attributes accordingly.
 *
 * Script Inputs:
 *    - "biographicMatchResult" (Object)  - Results of biographic attribute validation.
 *    - "capturedData"          (Object)  - Extracted user data from the government ID.
 *    - "objectAttributes"       (Object)  - User attributes stored in nodeState.
 *    - "username"              (String)  - The username of the user being verified.
 * 
 * Script Outputs:
 *    - "objectAttributes"       (Object)  - Updated user attributes stored in nodeState.
 *    - "errorMessage"           (String)  - Error message for user-facing issues.
 *    - "errorDetails"           (String)  - Detailed error information for debugging.
 * 
 * Outcomes:
 *    - "Success"            - Validation passed.
 *    - "Bio_Match_Failed"   - Biographic match failed.
 *    - "Error"              - Unexpected execution failure.
 */

try {
  // Presetting the outcome to `Success`;
  outcome = 'Success';

  // Check Biographic Match Result from Govt. Id card
  var biographicMatchResult = nodeState.get("biographicMatchResult");

  // Check if any match failed
  // Note: If some attribute is not captured from the ID card, expected response of biographicMatchResult should be 'NA'
  for (var identifier in biographicMatchResult) {
      if (biographicMatchResult[identifier] === 'false') { 
          // Update the outcome
          outcome = 'Bio_Match_Failed';
          break;
      }
  }

  if (outcome === 'Success') {
      // Get capturedData from shared state
      var capturedDataFromGovtId = nodeState.get("capturedData");
      var userAttributes = nodeState.get("objectAttributes");
      var username = nodeState.get("username");

      var registeredUserAttributes = openidm.query("managed/alpha_user",  
                             { "_queryFilter": `/userName eq "${username}"` }, 
                             ['givenName', 'sn']  
                            );

      var givenName = registeredUserAttributes.result[0].givenName;
      var sn = registeredUserAttributes.result[0].sn;

      userAttributes.put('givenName', givenName);
      userAttributes.put('sn', sn);

      var resultMapUserAttributes = {
        "firstName": "custom_legalFirstName",
        "lastName": "custom_legalLastName",
        "fullName": "custom_legalFullName",
        "addressStreet": "postalAddress",
        "addressCity": "city",
        "addressState": "stateProvince",
        "addressZip": "postalCode",
        "address": "postalAddress",
        "country": "country"
      };

      for(var key in capturedDataFromGovtId){
        if(resultMapUserAttributes.hasOwnProperty(key)) {
          userAttributes.put(resultMapUserAttributes[key], capturedDataFromGovtId[key]);
        }
      }

      nodeState.putShared("objectAttributes", userAttributes);  
  }

  action.goTo(outcome);
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");

    // Detailed error information for debugging
    nodeState.putShared("errorDetails", e.toString());

    // Go to Error
    action.goTo("Error");
}"

3802628c-9fd8-4833-b90a-a3c16cb5c4a1.script:
"/* Country Splitter
 *
 * Author: Ping Identity
 * Version : 1.0
 * Date: 03/28/2025
 * 
 * Splits journey execution path by country.
 * 
 * This script does not need to be parametrized. It will work properly as is.
 * 
 * Script Inputs:
 *    - "_id" (String) - User ID.
 *    - "objectAttributes" (Object) - Attributes associated with the user.
 *
 * Script Outputs:
 *    - "errorMessage" (String) - User-friendly error message if an error occurs.
 *    - "errorDetails" (String) - Detailed error message for debugging.
 *
 * Outcomes:
 *    - "Bermuda"
 *    - "Brazil"
 *    - "India"
 *    - "Ireland"
 *    - "Japan"
 *    - "Mexico"
 *    - "Philippines"
 *    - "Poland"
 *    - "United Kingdom"
 *    - "United States"
 *    - "Other" (Default for non-matching countries)
 *    - "error" (Unexpected failure)
 */

outcome = 'error';
try {
    var countryAmAttr = 'co';
    var countryIdmAttr = 'country';
    var countries = {
      '--': 'Other',
      'BM': 'Bermuda',
      'BR': 'Brazil',
      'IN': 'India',
      'IE': 'Ireland',
      'JP': 'Japan',
      'MX': 'Mexico',
      'PH': 'Philippines',
      'PL': 'Poland',
      'GB': 'United Kingdom',
      'US': 'United States',
    };
    var countryCodes = Object.keys(countries);
    var userid = nodeState.get('_id');
    var objectAttributes = nodeState.get('objectAttributes');
    var identity = idRepository.getIdentity(userid);
    var currentCountry = objectAttributes.get(countryIdmAttr) || identity.getAttributeValues(countryAmAttr)[0];
    if (currentCountry && countryCodes.indexOf(currentCountry) != -1) {
      outcome = countries[currentCountry];
    } else {
      outcome = 'other';
    }
} catch (e) {
    // Store a user-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Please contact support.");

    // Log the detailed error message for debugging
    nodeState.putShared("errorDetails", e.toString());

    action.goTo('error');
}"

