"/*
 * Credential Issuance Preparation Script
 * 
 * Author  : Ping Identity  
 * Version : 1.0  
 * Date    : 03/28/2025  
 * 
 * This script retrieves user attributes from the identity store and prepares verification proof for credential issuance.
 * 
 * Script Inputs:
 *    - "username"                   (String) - User identifier.
 *    - "pingOneVerificationMetaData" (Object) - PingOne Verify metadata.
 * 
 * Script Outputs:
 *    - User attributes               Attributes associated with the user.
 *    - "verificationProof"           (String) - Proof of verification.
 *    - "ialtype"                     (String) - Identity Assurance Level.
 * 
 * Outcomes:
 *    - "true"  (Success)
 *    - "Error" (Failure)
 */

try {
    // Retrieve the username from node state
    var username = nodeState.get('username');

    // Query user attributes from the identity store
    var registeredUserAttributes = openidm.query(
        "managed/alpha_user",
        { "_queryFilter": `/userName eq "${username}"` },
        [
            'givenName', 'sn', 'mail', 'telephoneNumber', 
            'postalAddress', 'city', 'stateProvince', 
            'postalCode', 'country'
        ]
    );

    // Extract the user's attributes  
    var userAttributes = registeredUserAttributes.result[0];  

    // Define the attributes that need to be stored in shared state
    var attributes = [
        "givenName", "sn", "mail", "telephoneNumber", 
        "postalAddress", "city", "stateProvince", 
        "postalCode", "country", "_id"
    ];  

    // Store user attributes to be issued to the wallet in shared state, if they are present
    attributes.forEach(attr => {
        if (userAttributes[attr]) {
            nodeState.putShared(attr === "_id" ? "p1AICUserId" : attr, userAttributes[attr]);
        }
    });

    // Set the Identity Assurance Level (IAL) to IAL2  
    nodeState.putShared("ialtype", "ial2");  

    // Retrieve PingOne verification metadata
    var pingOneVerificationMetaData = nodeState.get("pingOneVerificationMetaData");

    var pingOneVerifyTransactionId = pingOneVerificationMetaData.get("pingOneVerifyTransactionId");
    var pingOneVerifyTransactionStatus = pingOneVerificationMetaData.get("pingOneVerifyTransactionStatus");
    var verifiedDocuments = pingOneVerificationMetaData.get("verifiedDocuments");
    var pingOneVerifyPolicyId = pingOneVerificationMetaData.get("pingOneVerifyPolicyId");
    var pingOneVerifyTransactionCreatedAt = pingOneVerificationMetaData.get("pingOneVerifyTransactionCreatedAt");

    // Construct the verification proof object
    var verificationProof = {
        "verification": {
            "trust_framework": "nist_800_63A",
            "assurance_level": "ial2",
            "assurance_process": {
                "assurance_details": [
                    {
                        "assurance_type": "evidence_validation",
                        "assurance_classification": "strong",
                        "evidence_ref": [{
                            "txn": pingOneVerifyTransactionId
                        }]
                    },
                    {
                        "assurance_type": "verification",
                        "assurance_classification": "strong",
                        "evidence_ref": [{
                            "txn": pingOneVerifyTransactionId
                        }]
                    }
                ]
            },
            "time": pingOneVerifyTransactionCreatedAt,
            "verification_process": pingOneVerifyPolicyId,
            "evidence": {
                "transactionStatus": pingOneVerifyTransactionStatus,
                "transactionDocuments": verifiedDocuments
            }
        }
    };

    // Store verification proof in shared state
    nodeState.putShared("verificationProof", verificationProof);

    // Proceed to the success path
    action.goTo("true");
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");

    // Detailed error information for debugging
    nodeState.putShared("errorDetails", e.toString());

    // Go to Error
    action.goTo("Error");
}"

