"/*
 * Captured User Attributes Display Script
 * 
 * Author  : Ping Identity  
 * Version : 1.0  
 * Date    : 03/28/2025  
 * 
 * Retrieves user attributes from nodeState and displays them as read-only fields.
 * 
 * Script Inputs:
 *    - "objectAttributes" (Object) - User attributes map.
 * 
 * Script Outputs:
 *    - "errorMessage" (String) - User-friendly error message if an error occurs.
 *    - "errorDetails" (String) - Detailed error message for debugging.
 * 
 * Outcomes:
 *    - "true"  (Success)
 *    - "Error" (Failure)
 */

try {
    /**
     * Generates a script to make an input field read-only.
     * @param {string} key - The field label.
     * @returns {string} - JavaScript code to set the field as read-only.
     */
    function createScript(key) {
        return String(
            " \n\
              const fieldName = document.querySelector('input[data-vv-as=\""+ key +"\"]'); \n\
              fieldName.readOnly = true; \n\
        ");
    }

    var userAttributes = nodeState.get("objectAttributes");

    // Mapping of internal attribute names to display labels
    var mappingKeys = {
        'givenName': 'First Name',
        'sn': 'Last Name',
        'custom_legalFirstName': "Legal First Name",
        'custom_legalLastName': "Legal Last Name",
        'custom_legalFullName': "Legal Full Name",
        'postalAddress': 'Street Address',
        'city': 'City',
        'stateProvince': 'State',
        'postalCode': 'Zip Code',
        'country': 'Country',
    };

    if (callbacks.isEmpty()) {
        for (var key in mappingKeys) {
            if (key in userAttributes) {
                var value = userAttributes[key];

                // Add a text input field displaying the value for the attribute
                callbacksBuilder.stringAttributeInputCallback(mappingKeys[key], mappingKeys[key], value, false);

                // Make the input field read-only
                callbacksBuilder.scriptTextOutputCallback(createScript(mappingKeys[key]));
            }
        }
    }

    // Proceed to the success path
    action.goTo("true");
} catch (e) {
    // Store a user-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Please contact support.");

    // Log the detailed error message for debugging
    nodeState.putShared("errorDetails", e.toString());

