1dad0550-6205-45ee-84db-7f2b3cf80676.script:
"/*
 * Email Verification Status Updation & MFA Enrollment Script
 *
 * Author  : Ping Identity  
 * Version : 1.1  
 * Date    : 03/28/2025  
 *
 * This script marks emailVerified attribute and enrolls "EMAIL" as an MFA method if allowed.
 *
 * Script Inputs  : 
 *    - "_id" (String) - User ID  
 *    - "ciam_allowedMFATypes" (Array) - List of allowed MFA methods  
 *
 * Script Outputs : 
 *    - "errorMessage" (String) - User-friendly error message in case of failure  
 *    - "errorDetails" (String) - Detailed error message for debugging  
 *
 * Outcomes       : 
 *    - "true"  (Email MFA enrollment successful)  
 *    - "Error" (Unexpected execution failure)  
 *
 * Caution: This script may auto-enroll "EMAIL" as an MFA method in the user's profile.
 */

try {
    // Get the user ID
    var userId = nodeState.get("_id");

    // Mark the emailVerified attribute as verified
    openidm.patch("managed/alpha_user/" + userId, null, [
        { "operation": "replace", "field": "/custom_emailVerified", "value": "true" }
    ]);

    // Retrieve allowed MFA methods
    var allowedMFAMethods = nodeState.get("ciam_allowedMFATypes");

    // Fetch user's existing MFA devices
    var mfaDevicesObj = openidm.read("managed/alpha_user/" + userId, null, ["custom_mfaDevices"]);
    var mfaDevices = (mfaDevicesObj && mfaDevicesObj.custom_mfaDevices) ? mfaDevicesObj.custom_mfaDevices : [];

    // Convert MFA devices to an array format
    var usableMFADevices = [];

    mfaDevices.forEach(device => {
        usableMFADevices.push(device);
    });

    var emailMFAAllowed = false;
    for (var i = 0; i < allowedMFAMethods.length; i++) {
        if (allowedMFAMethods[i] === "EMAIL") {
            emailMFAAllowed = true;
            break;
        }
    }

    // Add EMAIL to MFA devices if allowed and not already present
    if (emailMFAAllowed && usableMFADevices.indexOf("EMAIL") === -1) {
        usableMFADevices.push("EMAIL");
    }

    // Update the MFA devices in the user profile
    openidm.patch("managed/alpha_user/" + userId, null, [
        { "operation": "replace", "field": "/custom_mfaDevices", "value": usableMFADevices }
    ]);

    // Proceed to success outcome
    action.goTo("true");
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");

    // Detailed error information for debugging
    nodeState.putShared("errorDetails", e.toString());

    // Go to Error
    action.goTo("Error");
}"

