27db6653-04bd-4338-ad88-4e2b7dbfcee1.script:
"/*
 * Username Input Validation Script
 *
 * Author  : Ping Identity  
 * Version : 1.1  
 * Date    : 03/28/2025  
 *
 * Description:  
 *    - This script ensures that the "Next" button remains disabled until the user provides a valid input.  
 *    - The script dynamically observes changes in the input field and enables the button once the input is non-empty.  
 *    - Prevents form submission if the input is empty at the time of submission.  
 *
 * Script Inputs  :  
 *    - null  
 *
 * Script Outputs :  
 *    - "errorMessage" (String) - User-friendly error message in case of failure  
 *    - "errorDetails" (String) - Detailed error message for debugging  
 *
 * Outcomes       :  
 *    - "true"  (Successfully enabled button for valid input)  
 *    - "Error" (An error occurred during execution)  
 */

try {
    // Disable next button, if input is empty
    function validateInput() {
        return String(" \n\
                    const submitBtn = document.querySelectorAll('button[type=\"submit\"]')[0]; \n\
                    if(submitBtn) submitBtn.disabled = true; \n\
                    function go(obs) { \n\
                        const username = document.querySelectorAll('input[data-vv-as=\"User Name\"]')[0]; \n\
                        if (username && submitBtn) { \n\
                            const enableSubmitBtn = () => { \n\
                                // Check input validity before enabling submit button \n\
                                const isValidInput = username.value.length > 0; \n\
                                if (isValidInput) { \n\
                                    submitBtn.disabled = false; \n\
                                } else { \n\
                                    submitBtn.disabled = true; \n\
                                } \n\
                            }; \n\
                            // Revalidate before form submission \n\
                            submitBtn.addEventListener('click', (event) => { \n\
                                const isValidInput = username.value.length > 0; \n\
                                if (!isValidInput) { \n\
                                    event.preventDefault(); \n\
                                    submitBtn.disabled = true; \n\
                                } \n\
                            }); \n\
                            username.addEventListener('input', enableSubmitBtn); \n\
                            if (obs) obs.disconnect(); \n\
                            return; \n\
                       } \n\
                      } \n\
                      if (document.querySelectorAll('input[data-vv-as=\"User Name\"]')[0]) go(); \n\
                      else { \n\
                        const observer = new MutationObserver((mutations, obs) => { go(obs); }); \n\
                        observer.observe(document, { childList: true, subtree: true }); \n\
                      } \n\
        ");
    }

    if (callbacks.isEmpty()) {                
        // Request callbacks
        callbacksBuilder.scriptTextOutputCallback(validateInput());
    }
    // Go to true
    action.goTo("true");
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");
    // Detailed error information for debugging
    nodeState.putShared("errorDetails", e.toString());
    // Go to Error 
    action.goTo("Error");
}
"

