"/*
 * OTP Collector Decision With Resend Option
 *
 * Author  : Ping Identity  
 * Version : 1.0  
 * Date    : 03/28/2025  
 *
 * Script Inputs  :  
 *    - "oneTimePassword" (String) - OTP retrieved from transient state  
 *    - "invalidOrExpiredCode" (String) - Error message for invalid OTP attempts  
 *
 * Script Outputs :  
 *    - "errorMessage" (String) - User-friendly error message in case of failure  
 *    - "errorDetails" (String) - Detailed error message for debugging  
 *
 * Outcomes       :  
 *    - "true"   (OTP is valid)  
 *    - "false"  (OTP is invalid)  
 *    - "resend" (User selected the resend option)  
 *    - "Error"  (An error occurred during execution)  
 */

try {
    // Delay before the "resend" button becomes enabled (in seconds)
    var DELAY = 5;

    /**
     * Generates the client-side script for OTP validation and resend timer.
     * @returns {string} JavaScript code for handling OTP validation and resend functionality.
     */
    function createScript() {
        return String(" \n\
            var otpLength = " + otpLength + "; \n\
            var COUNT = " + DELAY + "; \n\
            const submitBtn = document.querySelector('button[type=\"submit\"]'); \n\
            const resendTrigger = document.querySelector('input[data-vv-as=\"resendTrigger\"]'); \n\
            resendTrigger.style.display = \"none\"; \n\
            const label = document.querySelector('label[for=\"' + resendTrigger.id + '\"]'); \n\
            if (label) label.style.display = \"none\"; \n\
            const passcode = document.querySelector('input[data-vv-as=\"One Time Passcode\"]') || document.querySelector('input[data-vv-as=\"Code\"]'); \n\
            if (submitBtn) submitBtn.disabled = true; \n\
            \n\
            function validateCode() { \n\
                if (passcode) { \n\
                    passcode.setAttribute('maxlength', otpLength); \n\
                    passcode.setAttribute('pattern', '[0-9]{' + otpLength + '}'); \n\
                    passcode.setAttribute('inputmode', 'numeric'); \n\
                    const isValidCode = new RegExp('^[0-9]{' + otpLength + '}$').test(passcode.value); \n\
                    submitBtn.disabled = !isValidCode; \n\
                    return isValidCode; \n\
                } \n\
                return false; \n\
            }; \n\
            \n\
            function initResendButton(observer) { \n\
                if (passcode && submitBtn) { \n\
                    var btn = document.createElement('button'); \n\
                    btn.id = 'resendButton'; \n\
                    btn.classList.add(\"btn\", \"mt-3\", \"btn-secondary\", \"btn-sm\"); \n\
                    btn.onclick = function() { \n\
                        resendTrigger.value = '__RESEND__'; \n\
                        resendTrigger.dispatchEvent(new Event('input')); \n\
                    }; \n\
                    btn.innerHTML = 'Resend Code ... ' + COUNT + 's'; \n\
                    btn.disabled = true; \n\
                    passcode.parentNode.insertBefore(btn, passcode.nextSibling); \n\
                    \n\
                    var timer = setInterval(function() { \n\
                        if (COUNT == 1) { \n\
                            clearInterval(timer); \n\
                            btn.disabled = false; \n\
                            btn.innerHTML = 'Resend Code'; \n\
                        } else { \n\
                            COUNT--; \n\
                            btn.innerHTML = 'Resend Code ... ' + COUNT + 's'; \n\
                        } \n\
                    }, 1000); \n\
                    \n\
                    passcode.addEventListener('input', validateCode); \n\
                    submitBtn.addEventListener('click', (event) => { \n\
                        if (!validateCode()) { \n\
                            event.preventDefault(); \n\
                            submitBtn.disabled = true; \n\
                        } \n\
                    }); \n\
                    \n\
                    if (observer) observer.disconnect(); \n\
                } \n\
            } \n\
            \n\
            if (document.querySelector('input[data-vv-as=\"One Time Passcode\"]')) { \n\
                initResendButton(); \n\
            } else { \n\
                const observer = new MutationObserver((mutations, obs) => { \n\
                    initResendButton(obs); \n\
                }); \n\
                observer.observe(document, { childList: true, subtree: true }); \n\
            } \n\
        ");
    }

    // Retrieve invalid OTP error message and reset it
    var invalidOrExpiredCode = nodeState.get("invalidCodeErrorMessage");
    nodeState.putShared("invalidCodeErrorMessage", "");

    // Determine OTP length (default to 6 if not set)
    var otpLength = nodeState.get("oneTimePassword") ? nodeState.get("oneTimePassword").length : 6;

    if (callbacks.isEmpty()) {
        // Display error message if an invalid OTP was previously entered
        if (invalidOrExpiredCode && invalidOrExpiredCode.length) {
            callbacksBuilder.textOutputCallback(2, invalidOrExpiredCode);
        }
        callbacksBuilder.scriptTextOutputCallback(createScript());
        callbacksBuilder.nameCallback("One Time Passcode");
        callbacksBuilder.nameCallback("resendTrigger");
    } else {
        var otp = callbacks.getNameCallbacks().get(0);
        var resend = callbacks.getNameCallbacks().get(1);

        if (resend === '__RESEND__') {
            action.goTo("resend");
        } else {
            var sentOtp = nodeState.get("oneTimePassword");
            var isValid = sentOtp === otp;

            if (isValid) {
                action.goTo("true");
            } else {
                action.goTo("false");
            }
        }
    }
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");
    // Detailed error message for debugging
    nodeState.putShared("errorDetails", e.toString());
    // Go to Error  
    action.goTo("Error");
}
"
