479bfa88-b58f-46e9-9909-59f43a1109c9.script:
"/*
  - Data made available by nodes that have already executed are available in the sharedState variable.
  - The script should set outcome to either "true" or "false".
 */

/*
    - Script Inputs: "objectAttributes", "PingOneProtectEvaluationNode.RISK"
    - Script Outputs: "objectAttributes", "errorMessage", "errorDetails"
    - Outcomes: "true"
*/

try {
    function capitalizeFirstLetter(string) {
        return string
        .split(' ')                   // Split the string into an array of words
        .map(word => 
            word.charAt(0).toUpperCase() + word.slice(1)
        )                             // Capitalize the first letter of each word
        .join(' ');                   // Join the array back into a single string
        // return string.length > 0 ? string.charAt(0).toUpperCase() + string.slice(1) : "";
    }

    var attributes = nodeState.get("objectAttributes");

    var rawResponse = nodeState.get("PingOneProtectEvaluationNode.RISK");


    // Get & Set the protect activity variables in shared state
    var protectActivityCity = rawResponse.details.city;
    var protectActivityState = rawResponse.details.state;
    var protectDeviceOS = rawResponse.details.device.os.name;

    protectActivityCity = capitalizeFirstLetter(protectActivityCity);
    protectActivityState = capitalizeFirstLetter(protectActivityState);

    if (attributes) {
      attributes.put("custom_protectActivityCity", protectActivityCity);
      attributes.put("custom_protectActivityState", protectActivityState);
      attributes.put("custom_protectDeviceOS", protectDeviceOS);
      nodeState.putShared("objectAttributes", attributes);
    }
    // Go to true path
    action.goTo("true");
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");
    // Detailed error information for debugging
    nodeState.putShared("errorDetails", e.toString());
    // Go to Error  
    action.goTo("Error");
}

"

