34b32392-dd95-4fed-9e01-88fd6441dae7.script:
"/*
  - Data made available by nodes that have already executed are available in the sharedState variable.
  - The script should set outcome to either "true" or "false".
 */

/*
    - Script Inputs: "PingOneProtectEvaluationNode.RISK", "_id"
    - Script Outputs: "errorMessage", "errorDetails"
    - Outcomes: "highRisk", "newDevice", "low/medRisk"
*/

try {
    function capitalizeFirstLetter(string) {
        return string
        .split(' ')                   // Split the string into an array of words
        .map(word => 
            word.charAt(0).toUpperCase() + word.slice(1)
        )                             // Capitalize the first letter of each word
        .join(' ');                   // Join the array back into a single string
    }

    var rawResponse = nodeState.get("PingOneProtectEvaluationNode.RISK");

    var riskLevel = rawResponse.result.level;

    // Get & Set the protect activity variables in user's profile (to be used in notification templates)
    var protectActivityCity = rawResponse.details.city;
    var protectActivityState = rawResponse.details.state;
    var protectDeviceOS = rawResponse.details.device.os.name;

    protectActivityCity = capitalizeFirstLetter(protectActivityCity);
    protectActivityState = capitalizeFirstLetter(protectActivityState);


    // Get the userId from nodestate
    var userId = nodeState.get("_id");

    // Update the protectActvityCity attribute of user
    openidm.patch("managed/alpha_user/" + userId, null, [{"operation":"replace", "field":"/custom_protectActivityCity", "value":protectActivityCity}]);

    openidm.patch("managed/alpha_user/" + userId, null, [{"operation":"replace", "field":"/custom_protectActivityState", "value":protectActivityState}]);

    openidm.patch("managed/alpha_user/" + userId, null, [{"operation":"replace", "field":"/custom_protectDeviceOS", "value":protectDeviceOS}]);

    if(riskLevel === "HIGH") {
        outcome = "highRisk";
        action.goTo(outcome);
    }

    // Check if new device is found
    if(rawResponse.details.newDevice.status === "UNKNOWN_DEVICE") outcome = "newDevice";
    else outcome = "low/medRisk";

    // Go to outcome
    action.goTo(outcome);
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");
    // Detailed error information for debugging
    nodeState.putShared("errorDetails", e.toString());
    // Go to Error  
    action.goTo("Error");
}"

390bdf79-fadd-404f-9422-29823b7dce8a.script:
"/*
  - Data made available by nodes that have already executed are available in the sharedState variable.
  - The script should set outcome to either "true" or "false".
 */

/*
    - Script Inputs: "p1ProtectAction"
    - Script Outputs: "errorMessage", "errorDetails"
    - Outcomes: "init", "eval"
*/

try {
    // Only two state variables are expected to be put in shared state in the main journey: 
    // "init" -> To initialize the P1 Protect to collect Risk SDK signals & 
    // "eval" -> To send the Risk SDK signals to protect and evalute them
    var p1ProtectAction = nodeState.get("p1ProtectAction");

    if(p1ProtectAction === "init") action.goTo("init");
    else action.goTo("eval");
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");
    // Detailed error information for debugging
    nodeState.putShared("errorDetails", e.toString());
    // Go to Error  
    action.goTo("Error");
}"

