    function validateInput() {
        return String(" \n\
                    // Validate the verification code \n\
                    function validateCode() { \n\
                        const submitBtn = document.querySelectorAll('button[type=\"button\"]')[0]; \n\
                        const verificationCode = document.querySelectorAll('input[data-vv-as=\"Enter verification code\"]')[0]; \n\
                        if (verificationCode) { \n\
                            // Set input properties to prevent user tampering \n\
