d5242e7f-60b1-4314-93e9-89b80f7c5bf6.script:
"/*
  - Data made available by nodes that have already executed are available in the sharedState variable.
  - The script should set outcome to either "true" or "false".
 */

/*
    - Script Inputs: "_id", "ciam_allowedMFATypes"
    - Script Outputs: "errorMessage", "errorDetails"
    - Outcomes: "true"
*/

// Caution: This script might auto-enroll the "EMAIL" as a MFA method in user's profile


try {
    // Get the userId
    var userId = nodeState.get("_id");

    // Update the email verification status attribute of user to "true"
    openidm.patch("managed/alpha_user/" + userId, null, [{"operation":"replace", "field":"/custom_emailVerified", "value":"true"}]);

    // Enroll email as an MFA authentication method
    var allowedMFAMethods = nodeState.get("ciam_allowedMFATypes");
    var mfaDevicesObj = openidm.read("managed/alpha_user/" + userId, null, ["custom_mfaDevices"]);
    var mfaDevices = (mfaDevicesObj && mfaDevicesObj.custom_mfaDevices) ? mfaDevicesObj.custom_mfaDevices : [];

    // By default returned mfaDevices is of Object type so values are parsed in a new array
    var usableMFADevices = [];

    mfaDevices.forEach(device => {
        usableMFADevices.push(device);
    });

    var emailMFAAllowed = false;
    allowedMFAMethods.forEach(device => {
        if(device === "EMAIL") emailMFAAllowed = true;
    });

    // Add email to the MFA Devices list, if not already present 
    if(emailMFAAllowed && !usableMFADevices.includes("EMAIL")) usableMFADevices.push("EMAIL");

    // Update the custom_mfaDevices Object
    openidm.patch("managed/alpha_user/" + userId, null, [{"operation":"replace", "field":"/custom_mfaDevices", "value":usableMFADevices}]);
    // Go to true path
    action.goTo("true");
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");
    // Detailed error information for debugging
    nodeState.putShared("errorDetails", e.toString());
    // Go to Error
    action.goTo("Error");
}
"

