678ed3e6-ea15-4016-9a67-fe2d3e76dae1.script:
"/*
  - Data made available by nodes that have already executed are available in the sharedState variable.
  - The script should set outcome to either "true" or "false".
 */

/*
    - Script Inputs: null
    - Script Outputs: "errorMessage", "errorDetails"
    - Outcomes: "true"
*/

try {
    // Validate Inputs
    function validateInput() {
        return String(" \n\
                    const firstName = document.querySelectorAll('input[data-vv-as=\"First Name\"]')[0]; \n\
                    const lastName = document.querySelectorAll('input[data-vv-as=\"Last Name\"]')[0]; \n\
                    const emailAddress = document.querySelectorAll('input[data-vv-as=\"Email Address\"]')[0]; \n\
                    const submitBtn = document.querySelectorAll('button[type=\"submit\"]')[0]; \n\
                    \n\
                    if (firstName) firstName.setAttribute('maxlength', '255'); \n\
                    if (lastName) lastName.setAttribute('maxlength', '255'); \n\
                    if (emailAddress) emailAddress.setAttribute('maxlength', '255'); \n\
                    \n\
                    if (submitBtn) submitBtn.disabled = true; \n\
                    function validateFields() { \n\
                        const isValid = firstName.value.length > 0 && lastName.value.length > 0 && emailAddress.value.length > 0; \n\
                        submitBtn.disabled = !isValid; \n\
                    } \n\
                    \n\
                    submitBtn.addEventListener('click', (event) => { \n\
                        // Ensure the fields are not exceeding max length even if modified in browser \n\
                        if (firstName.value.length > 255 || lastName.value.length > 255 || emailAddress.value.length > 255) { \n\
                            event.preventDefault(); \n\
                            submitBtn.disabled = true; \n\
                        } \n\
                        const isValid = firstName.value.length > 0 && lastName.value.length > 0 && emailAddress.value.length > 0; \n\
                        if (!isValid) { \n\
                            event.preventDefault(); \n\
                            submitBtn.disabled = true; \n\
                        } \n\
                    }); \n\
                    \n\
                    firstName.addEventListener('input', validateFields); \n\
                    lastName.addEventListener('input', validateFields); \n\
                    emailAddress.addEventListener('input', validateFields); \n\
        ");
    }

    if (callbacks.isEmpty()) {                
        // Request callbacks
        callbacksBuilder.scriptTextOutputCallback(validateInput());
    }
    // Go to true path
    action.goTo("true");
} catch (e) {
    // User-friendly error message
    nodeState.putShared("errorMessage", "An error has occurred. Contact support.");
    // Detailed error information for debugging
    nodeState.putShared("errorDetails", e.toString());
    // Go to Error  
    action.goTo("Error");
}"
